INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (41, '2018年国防科学技术大学考研复试分数线', '\n\r\n	　　国防科大各学院2018年硕士研究生入学考试复试分数线如下。\n\r\n	　　备注：军队在职干部、军队院校应届本科毕业生、国防生考生、符合“军队英模烈子女成才培养工程”的考生符合军队相关加分政策的，享受加分政策后达到复试分数线者可进入复试。\n\n\n', 19);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (42, '2018年国防科技大学考研复试分数线公布通知', '\n\r\n	　　2018考研初试结束后，初试成绩查询及2018年国防科技大学考研复试分数线公布时间成为考生关注的重点。国防科技大学不属于34所自主划线院校，根据往年国防科技大学考研复试分数线公布时间推定，2018年国防科技大学考研复试分数线公布时间：预计于3月中旬公布。\n\r\n	　　为及时获知国防科技大学考研复试分数线公布时间变化，请广大考生密切关注考研帮2018年考研分数线专题，及时获取考研复试分数线变化，为之后的复试及调剂打好提前战。\n\r\n	　　考研帮会及时更新各大院校考研复试分数线相关信息，更多考研资讯请关注考研帮APP及网站公告，预祝大家考研成功！\n\r\n	　　更多需要关注的考研成绩查询信息：\n\r\n	　　＞＞＞2018考研分数线\n\r\n	　　＞＞＞2018考研国家线预测及解读\n\r\n	　　＞＞＞历年学硕考研国家线\n\r\n	　　＞＞＞历年专硕考研国家线\n\r\n	 \n', 19);
