INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (194, '2017年中南财经政法大学考研复试分数线公布通知', '\n\r\n	　　2017年中南财经政法大学考研复试分数线公布时间：预计于3月中旬公布（据：往年中南财经政法大学考研复试分数线公布时间推定）。\n\r\n	　　中南财经政法大学不属于34所自主划线院校，历年考研复试分数线参看考研国家线，2017年考研国家分数线预计在3月中旬发布。\n\r\n	　　请广大考生密切关注考研帮：2017年考研复试分数线专题，及时了解中南财经政法大学考研复试分数线的相关变化，为下一步复试及调剂择校做好准备。\n\r\n	　　考研帮会及时更新考研分数线相关信息，更多考研资讯请关注网站更新，预祝大家考研成功！\n\r\n	　　＞＞＞2017考研分数线\n\r\n	　　＞＞＞2017考研国家线预测\n\r\n	　　＞＞＞历年学硕国家线\n\r\n	　　＞＞＞历年专硕国家线\n', 43);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (195, '2018考研：中南财经政法大学法律（非法学）考研分数线走势', '\n\r\n	　　【摘要】中南财经政法大学作为老牌法律院校，法学实力强，又因为地处中部，较之东部热门院校报考难度要小。为帮助考研er更好了解该院校，并做好充分的备考准备，考研帮整理中南财经政法大学2012-2016年不同的分数要求，并对其变化作出分析以预测2017年的分数走向。\n\r\n	　　▶介绍\n\r\n	　　中南财经政法大学是名牌法律院校“五院四系”之一，自从合并后法学教育曾出现下滑，但是获得民商法全国重点学科，以及成为知识产权研究基地，法学恢复较多，实力要比中大等强，但区位上优势不明显。此外，虽然综合就业、学校知名度、培养情况比不上中大和复旦，但合并后得到更多的校友，就业也相对乐观，特别是金融和经济方向的法律工作者。\n\r\n	　　▶数据分析\n\r\n	　　中南财经政法大学法律硕士（非法学）专业近五年的复试分数线要求如下：\n\n\n\n\n\n\n\n\n\n\n\n\n\r\n				中南财经政法大学2012-2016年复试分数要求                                                      \n\n\n\r\n				单科（满分=100分）/单科（满分>100分）/总分\n\n\n\r\n				 \n\r\n				法学院\n\r\n				国家线\n\n\n\r\n				2016\n\r\n				45\n\r\n				68\n\r\n				315\n\r\n				45\n\r\n				68\n\r\n				315\n\n\n\r\n				2015\n\r\n				42\n\r\n				63\n\r\n				311\n\r\n				42\n\r\n				63\n\r\n				300\n\n\n\r\n				2014\n\r\n				44\n\r\n				66\n\r\n				325\n\r\n				44\n\r\n				66\n\r\n				315\n\n\n\r\n				2013\n\r\n				42\n\r\n				63\n\r\n				335\n\r\n				42\n\r\n				63\n\r\n				315\n\n\n\r\n				2012\n\r\n				42\n\r\n				63\n\r\n				330\n\r\n				42\n\r\n				63\n\r\n				315\n\n\n\n\n\n\r\n	　　从上面的数据可以看出，中南财经政法大学的英语、政治、专业课的复试分数线基本以国家线为标准，在准备政治、英语的过程中难度相对较低；总分上的要求一般高出国家线11~20分，16年与国家线持平。从近年的趋势来看，分数要求呈现下降趋势，报考难度相对较小，预计17年总分要求会高出国家线5~10分左右。\n\r\n	　　▶考研帮说：本文系考研帮精心整理，转载请注明出处。\n', 43);
