INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (222, '2018年华南师范大学考研复试分数线公布通知', '\n\r\n	　　2018考研初试结束后，初试成绩查询及2018年华南师范大学考研复试分数线公布时间成为考生关注的重点。华南师范大学不属于34所自主划线院校，根据往年华南师范大学考研复试分数线公布时间推定，2018年华南师范大学考研复试分数线公布时间：预计于3月中旬公布。\n\r\n	　　为及时获知华南师范大学考研复试分数线公布时间变化，请广大考生密切关注考研帮2018年考研分数线专题，及时获取考研复试分数线变化，为之后的复试及调剂打好提前战。\n\r\n	　　考研帮会及时更新各大院校考研复试分数线相关信息，更多考研资讯请关注考研帮APP及网站公告，预祝大家考研成功！\n\r\n	　　更多需要关注的考研成绩查询信息：\n\r\n	　　＞＞＞2018考研分数线\n\r\n	　　＞＞＞2018考研国家线预测及解读\n\r\n	　　＞＞＞历年学硕考研国家线\n\r\n	　　＞＞＞历年专硕考研国家线\n\r\n	 \n', 17);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (223, '2017年华南师范大学考研复试分数线', '\n\r\n	　　复试资格要求\n\r\n	　　（1）普通考生：初试成绩符合第一志愿报考专业的国家一区分数线。按照1:1.2-1:1.5左右，最高不超过1:1.8的差额比例确定复试资格名单。\n\r\n	　　（2）专项计划：我校“大学生士兵计划”考生第一志愿与调剂进入复试的初试成绩要求相同。具体如下：\n\n\n\n\n专项计划\n\n\n招生计划数\n\n\n\n总分\n\n\n\n单科（满分=100分）\n\n\n\n单科（满分>100分）\n\n\n\n适用专业\n\n\n\n\n\n退役大学生士兵专项计划\n\n\n\n20\n\n\n\n245\n\n\n\n30\n\n\n\n45\n\n\n\n除工商管理、公共管理、旅游管理、工程管理外的各专业\n\n\n\n\n\n130\n\n\n\n30\n\n\n\n45\n\n\n\n工商管理、公共管理、旅游管理、工程管理\n\n\n\n\n', 17);
