INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (344, '2018年西安电子科技大学空间科学与技术学院考研复试分数线', '\n\r\n	　　1.符合以下分数线要求的考生可以参加报考学科的复试\n\n\r\n	　　2.优研计划合格生源复试线为：\n\r\n	　　（1）学业一贯表现突出的学生和获得高水平学科竞赛奖的学生：初试成绩通过A类地区国家线即可进入复试；\n\r\n	　　（2）获得高水平研究成果的学生：初试成绩须通过A类地区国家线，并且总分达到国家线上20分或学院所在学科/领域复试线即可进入复试；\n\r\n	　　3.未达到报考学科复试分数线的考生若达到航天工程领域复试分数线，可进行院内调剂参加航天工程领域复试。\n\r\n	　　4.推荐免试学生已进行过复试，本次不再参加。\n', 27);
