INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (421, '2017年中国地质大学(武汉)考研复试分数线公布通知', '\n\r\n	　　2017年中国地质大学(武汉)考研复试分数线公布时间：预计于3月中旬公布（据：往年中国地质大学(武汉)考研复试分数线公布时间推定）。\n\r\n	　　中国地质大学(武汉)不属于34所自主划线院校，历年考研复试分数线参看考研国家线，2017年考研国家分数线预计在3月中旬发布。\n\r\n	　　请广大考生密切关注考研帮：2017年考研复试分数线专题，及时了解中国地质大学(武汉)考研复试分数线的相关变化，为下一步复试及调剂择校做好准备。\n\r\n	　　考研帮会及时更新考研分数线相关信息，更多考研资讯请关注网站更新，预祝大家考研成功！\n\r\n	　　＞＞＞2017考研分数线\n\r\n	　　＞＞＞2017考研国家线预测\n\r\n	　　＞＞＞历年学硕国家线\n\r\n	　　＞＞＞历年专硕国家线\n', 4);
