INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (517, '2018年湖南师范大学考研复试分数线', '\n\n　　一、2018年全国硕士研究生招生考试考生进入复试的初试成绩基本要求(学术学位类)\n\r\n	 \n\n　　二、2018年全国硕士研究生招生考试考生进入复试的初试成绩基本要求(专业学位类)\n\n\r\n	　　各招生专业在不低于上述基本分数要求的前提下，复试比例根据上线生源情况，原则上控制在“120%-150%”之间。同时招收全日制和非全日制的专业，根据全日制和非全日制的总计划，按适当的复试比例确定同一复试分数线。\n\n　　三、单考考生进入复试的初试成绩要求：\n\r\n	　　总分260分，单科：30分（满分=100分）、45分（满分=150）、90分（满分=300分）。\n\r\n	　　报考“退役大学生士兵计划”考生进入复试的初试成绩要求：\n\r\n	　　管理类联考：总分135分，单科：30分（满分=100分）、60分（满分=200）。\n\r\n	　　其他专业：总分240分，单科：30分（满分=100分）、45分（满分=150）、90分（满分=300分）。\n\r\n	　　我校今年临床医学专业学位硕士研究生初试成绩基本要求与教育部划定的初试成绩基本要求相同。\r\n	 \n', 35);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (518, '2018年湖南师范大学考研初试总分排名查询', '\n\r\n	　　各位考生：\n\r\n	　　近日，教育部公布了2018年全国硕士研究生招生初试成绩基本要求。现对上国家线的考生按总分在所报考专业内进行了排序，请到研究生院首页公共信息平台中的招生考试成绩查询系统中查询。如下图：\n\n\r\n	　　说明：本排名为上国家线考生总分从高到低在报考学院报考专业内的排序（排名并未区分全日制与非全日制考生）。\n\r\n	　　湖南师范大学研究生院考务办公室\n\r\n	　　2018年3月18日\n\r\n	 \n', 35);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (519, '2018年湖南师范大学考研复试分数线公布通知', '\n\r\n	　　2018考研初试结束后，初试成绩查询及2018年湖南师范大学考研复试分数线公布时间成为考生关注的重点。湖南师范大学不属于34所自主划线院校，根据往年湖南师范大学考研复试分数线公布时间推定，2018年湖南师范大学考研复试分数线公布时间：预计于3月中旬公布。\n\r\n	　　为及时获知湖南师范大学考研复试分数线公布时间变化，请广大考生密切关注考研帮2018年考研分数线专题，及时获取考研复试分数线变化，为之后的复试及调剂打好提前战。\n\r\n	　　考研帮会及时更新各大院校考研复试分数线相关信息，更多考研资讯请关注考研帮APP及网站公告，预祝大家考研成功！\n\r\n	　　更多需要关注的考研成绩查询信息：\n\r\n	　　＞＞＞2018考研分数线\n\r\n	　　＞＞＞2018考研国家线预测及解读\n\r\n	　　＞＞＞历年学硕考研国家线\n\r\n	　　＞＞＞历年专硕考研国家线\n\r\n	 \n', 35);
