INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (539, '2018年中国石油大学（华东）石油工程学院考研复试分数线', '\n\r\n	　　我院油气田开发工程专业复试比例为1:1.5，其他专业复试比例为1:1.3。\n\r\n	　　根据各专业上线生源情况，油气井工程、油气田开发工程、船舶与海洋结构物设计制造、石油与天然气工程、船舶与海洋工程专业复试分数线同国家线，通过国家线者即可参加上述报考专业的复试；海洋油气工程专业复试分数线为：总分313分，单科（满分=100分）34分、单科（满分=150分）51分。\n', 40);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (540, '2018年中国石油大学（华东）考研复试分数线（各学院汇总）', '\n\r\n	　　为了让大家对于中国石油大学（华东）复试分数线有所了解，提早准备复试及调剂相关事项，提早为2019考研择校做好准备，考研帮为大家整理了中国石油大学（华东）各学院的考研复试分数线。\n\r\n	　　2018年中国石油大学（华东）各培养单位考研复试分数线汇总如下：\n\n\n\n\n\n学 院 名 称 \n\n\n\n复试分数线 \n\n\n\n\n\n储运与建筑工程学院\n\n\n\r\n					复试分数线：点击查看 \n\n\n\n\n\n机电工程学院 \n\n\n\r\n					复试分数线：点击查看 \n\n\n\n\n\n文学院 \n\n\n\r\n					复试分数线：点击查看 \n\n\n\n\n\n体育学硕士 \n\n\n\r\n					复试分数线：点击查看 \n\n\n\n\n\n马克思主义学院 \n\n\n\r\n					复试分数线：点击查看\n\n\n\n\n\n石油工程学院 \n\n\n\r\n					复试分数线：点击查看\n\n\n\n\n\n地学院 \n\n\n\r\n					复试分数线：点击查看\n\n\n\n\n\n理学院 \n\n\n\r\n					复试分数线：点击查看\n\n\n\n\n', 40);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (541, '2018年中国石油大学（华东）考研复试分数线公布通知', '\n\r\n	　　2018考研初试结束后，初试成绩查询及2018年中国石油大学（华东）考研复试分数线公布时间成为考生关注的重点。中国石油大学（华东）不属于34所自主划线院校，根据往年中国石油大学（华东）考研复试分数线公布时间推定，2018年中国石油大学（华东）考研复试分数线公布时间：预计于3月中旬公布。\n\r\n	　　为及时获知中国石油大学（华东）考研复试分数线公布时间变化，请广大考生密切关注考研帮2018年考研分数线专题，及时获取考研复试分数线变化，为之后的复试及调剂打好提前战。\n\r\n	　　考研帮会及时更新各大院校考研复试分数线相关信息，更多考研资讯请关注考研帮APP及网站公告，预祝大家考研成功！\n\r\n	　　更多需要关注的考研成绩查询信息：\n\r\n	　　＞＞＞2018考研分数线\n\r\n	　　＞＞＞2018考研国家线预测及解读\n\r\n	　　＞＞＞历年学硕考研国家线\n\r\n	　　＞＞＞历年专硕考研国家线\n\r\n	 \n\r\n	【34所自主划线】\n', 40);
