INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (598, '2018年武汉大学考研复试分数线公布通知', '\n\r\n	　　2018考研初试结束后，初试成绩查询及2018年武汉大学考研复试分数线公布时间成为考生关注的重点。武汉大学属于34所自主划线院校，根据往年武汉大学考研复试分数线公布时间推定，2018年武汉大学考研复试分数线公布时间：3月5日。\n\r\n	　　为及时获知武汉大学考研复试分数线公布时间变化，请广大考生密切关注考研帮2018年考研分数线专题，及时获取考研复试分数线变化，为之后的复试及调剂打好提前战。\n\r\n	　　考研帮会及时更新各大院校考研复试分数线相关信息，更多考研资讯请关注考研帮APP及网站公告，预祝大家考研成功！\n\r\n	　　更多需要关注的考研成绩查询信息：\n\r\n	　　＞＞＞2018考研分数线\n\r\n	　　＞＞＞历年34所复试分数线\n\r\n	 \n', 7);
