INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (697, '2018年广西大学考研复试分数线', '\n\r\n	　　根据2018年全国硕士研究生招生考试考生初试成绩和我校各学院一级学科（专业领域）的招生计划，我校进入复试分数线基本要求除下表所列特殊情况外，均执行“2018年全国硕士研究生招生考试考生进入复试的初试成绩基本要求（B类考生）”。\n\n\n\n\n\n部分一级学科/领域/专项计划进入复试的考生初试分数基本要求\n\n\n\n\n\r\n					一级学科/领域/专项计划\n\n\n\r\n					普通考生\n\n\n\r\n					骨干计划考生\n\n\n\r\n					士兵计划考生\n\n\n\n\n\n1204公共管理\n\n\n\r\n					总分≥328，英语二≥50\n\n\n\r\n					总分≥313，英语二≥40\n\n\n\r\n					总分≥278，英语二≥40\n\n\n\n\n\n1253会计（含全日制、非全日制）\n\n\n\r\n					总分≥ 203分，管理类联考≥ 120分，英语二≥60分 \n\n\n\r\n					总分≥ 188分，管理类联考≥ 110分，英语二≥50分\n\n\n\r\n					总分≥ 153分，管理类联考≥ 110分，英语二≥50分 \n\n\n\n\n\n0202应用经济学\n\n\r\n				 \n\n\r\n					少数民族高层次人才骨干计划考生总分≥320\n\r\n					享受少数民族政策考生总分≥300\n\n\n\n\n\n1202工商管理\n\n\n\n\n\r\n					退役大学生士兵专项招生计划\n\n\n\r\n					总分≥报考专业复试线成绩下降50分，单科成绩≥报考专业复试线成绩下降10分\n\n\n\n\n\r\n					少数民族高层次人才骨干计划\n\n\n\r\n					总分≥报考专业复试线成绩下降15分，单科成绩≥报考专业复试线成绩下降10分\n\n\n\n\n\n\r\n	　　特此说明。\n\r\n	　　广西大学研究生院\n\r\n	　　2018年3月22日\n', 33);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (698, '2018年广西大学考研复试分数线公布通知', '\n\r\n	　　2018考研初试结束后，初试成绩查询及2018年广西大学考研复试分数线公布时间成为考生关注的重点。广西大学不属于34所自主划线院校，根据往年广西大学考研复试分数线公布时间推定，2018年广西大学考研复试分数线公布时间：预计于3月中旬公布。\n\r\n	　　为及时获知广西大学考研复试分数线公布时间变化，请广大考生密切关注考研帮2018年考研分数线专题，及时获取考研复试分数线变化，为之后的复试及调剂打好提前战。\n\r\n	　　考研帮会及时更新各大院校考研复试分数线相关信息，更多考研资讯请关注考研帮APP及网站公告，预祝大家考研成功！\n\r\n	　　更多需要关注的考研成绩查询信息：\n\r\n	　　＞＞＞2018考研分数线\n\r\n	　　＞＞＞2018考研国家线预测及解读\n\r\n	　　＞＞＞历年学硕考研国家线\n\r\n	　　＞＞＞历年专硕考研国家线\n\r\n	 \n', 33);
