INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (728, '2018年中南大学考研复试分数线', '\n\r\n	　　根据2018年硕士研究生入学考试考生成绩统计结果和我校各院系招生计划，经学校研究生招生工作领导小组讨论决定，对入围复试的考生初试分数基本要求如下（报考全日制及非全日制考生执行以下同一分数要求）。\n\n\n\n\n\n报考学科门类（专业）或专业学位类别或专项计划 \n\n([]内为学科门类代码或专业代码前几位) \n\n\n\r\n					总分 \n\n\n\r\n					单科1\n\r\n					（总分=100） \n\n\n\r\n					单科2\n\r\n					（总分>100） \n\n\n\n\n\r\n					哲学[01]\n\n\n\n300\n\n\n\n45\n\n\n\n75\n\n\n\n\n\r\n					经济学[02] \n\n\n\n340\n\n\n\n50\n\n\n\n80\n\n\n\n\n\r\n					法学[03]（不含[035]） \n\n\n\n335\n\n\n\n50\n\n\n\n80\n\n\n\n\n\r\n					专业学位法律[0351]、社会类[0352] \n\n\n\n315\n\n\n\n45\n\n\n\n75\n\n\n\n\n\r\n					心理学[0402]\n\n\n\n345\n\n\n\n60\n\n\n\n180\n\n\n\n\n\r\n					专业学位教育类[045]\n\n\n\n345\n\n\n\n50\n\n\n\n80\n\n\n\n\n\r\n					文学[05]\n\n\n\n360\n\n\n\n60\n\n\n\n90\n\n\n\n\n\r\n					理学[07]\n\n\n\n300\n\n\n\n45\n\n\n\n75\n\n\n\n\n\r\n					工学[08]（不含工学照顾专业、工程照顾领域） \n\n\n\n305\n\n\n\n45\n\n\n\n70\n\n\n\n\n\r\n					工学照顾专业及工程照顾领域[0801]、[0806]、[0807]、[0818]、[0819]、[0825]、[085205]、[085206]、[085217]、[085218]、[085224]\n\n\n\n300\n\n\n\n45\n\n\n\n70\n\n\n\n\n\r\n					医学[10]（不含基础医学[1001]） \n\n\n\n310\n\n\n\n50\n\n\n\n150\n\n\n\n\n\r\n					基础医学[1001]\n\n\n\n295\n\n\n\n50\n\n\n\n120\n\n\n\n\n\r\n					管理学[12]（不含专业学位管理类[125]） \n\n\n\n350\n\n\n\n55\n\n\n\n80\n\n\n\n\n\r\n					专业学位管理类[125]\n\n\n\r\n					执行2018年国家A类分数线 \n\n\n\n\n\r\n					艺术学[13]\n\n\n\n350\n\n\n\n45\n\n\n\n90\n\n\n\n\n\r\n					单独考试 \n\n\n\n320\n\n\n\n52\n\n\n\n70\n\n\n\n\n\r\n					骨干计划 \n\n\n\r\n					报考专业控制线总分下降20分，\n\r\n					单科线下降10分 \n\n\n\n\n\r\n					士兵计划 \n\n\n\r\n					报考专业控制线总分下降50分，\n\r\n					单科线下降10分\n\n\n\n\n\r\n	　　说明：\n\r\n	　　1、我校实行差额复试，具体安排由相关学院通知，请达到初试成绩基本要求的考生做好复试准备。\n\r\n	　　2、享受加分考生申请条件：\n\r\n	　　①参加“大学生志愿服务西部计划”“三支一扶计划”“农村义务教育阶段学校教师特设岗位计划”“赴外汉语教师志愿者”等项目服务期满、考核合格的考生，3年内参加全国硕士研究生招生考试的初试总分加10分。\n\r\n	　　②高校学生应征入伍服义务兵役退役，达到报考条件后，3年内参加全国硕士研究生招生考试的考生，初试总分加10 分。\n\r\n	　　③参加“选聘高校毕业生到村任职”项目服务期满、考核称职以上的考生，3年内参加全国硕士研究生招生考试的，初试总分加10 分，其中报考人文社科类专业研究生的，初试总分加15 分。\n\r\n	　　符合以上加分条件的考生须在 3 月 10 日前向我校研招办提出书面申请，并出具相关证明材料。可发电子邮件，邮箱为803097@csu.edu.cn。\n\r\n	　　中南大学\n\r\n	　　2018年3月6日\n', 10);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (729, '2018年中南大学考研复试分数线公布通知', '\n\r\n	　　2018考研初试结束后，初试成绩查询及2018年中南大学考研复试分数线公布时间成为考生关注的重点。中南大学属于34所自主划线院校，根据往年中南大学考研复试分数线公布时间推定，2018年中南大学考研复试分数线公布时间：3月中旬。\n\r\n	　　为及时获知中南大学考研复试分数线公布时间变化，请广大考生密切关注考研帮2018年考研分数线专题，及时获取考研复试分数线变化，为之后的复试及调剂打好提前战。\n\r\n	　　考研帮会及时更新各大院校考研复试分数线相关信息，更多考研资讯请关注考研帮APP及网站公告，预祝大家考研成功！\n\r\n	　　更多需要关注的考研成绩查询信息：\n\r\n	　　＞＞＞2018考研分数线\n\r\n	　　＞＞＞历年34所复试分数线\n\r\n	 \n', 10);
INSERT INTO `grades` (`id`, `title`, `content`, `sId`) VALUES (730, '2011-2017年中南大学【哲学】考研复试分数线趋势', '\n\r\n	　　在备考过程中，无论在择校阶段还是之后的复试阶段，目标院校分数线都会备受关注，考研er通过了解分数线情况，一方面确定自己的备考目标，另一方面明确自己的备考难度，考研路上不打无准备之战。\n\r\n	　　关于分数线，小伙伴们要关注是哪个分数：单科【满分=100分】分数线（如政治/英语）、单科【满分≥100分】分数线（如数学/专业课）、总分分数线，因为只有这三个分数都过线，才能获取复试资格。\n\r\n	　　为此，考研帮整理了11-17年中南大学【哲学】学科历年复试分数线趋势图，希望对大家有所帮助。\n\r\n	　　▶中南大学【哲学】总分分数线走势\n\r\n	　　▶中南大学【哲学】单科【满分=100分】分数线走势\n\r\n	　　▶中南大学【哲学】单科【满分≥100分】分数线走势\n\r\n	　　▶数据说明：\n\r\n	　　（1）分数线均来自学校官方发布的招生考试复试资格基本线，\r\n	　　（2）对于该学科专业课一/二单科分数要求不同时，帮帮提供最低分数线，\r\n	　　（3）各院系可根据本学科、专业特点及生源和计划数制定不低于学校基本要求的本院系学科、专业复试基本要求，即具体专业的复试线会高出学校复试基础线。\n\r\n	　　▶友情提示：\n\r\n	　　考研信息数量巨大，整理过程中难免出错，如您发现问题，欢迎评论留言指正。\r\n	 \n', 10);
