INSERT INTO `outline` (`id`, `title`, `content`, `sId`) VALUES (82, '2014年中山大学《833微观经济学与管理学》考研大纲', '\n\r\n	　　1、微观经济学的基本概念、基本理论及其应用。\r\n	　　2、管理学基本概念、原理、方法及其实践性应用。\r\n	　　3、当今世界管理环境、管理学主要理论流派、计划与战略、创业与创新、组织理论、人力资源规划、领导与激励理论、控制论等内容。\n', 25);
INSERT INTO `outline` (`id`, `title`, `content`, `sId`) VALUES (83, '2014年中山大学《社会工作原理》考研大纲', '\n\r\n	 \n\r\n	　　社会工作原理部分包括社会工作基础知识及社会学基础知识两个方面的知识。\n\r\n	　　第一部分：社会工作基础知识\r\n	　　了解社会工作的产生背景及其基本假设；了解社会工作的社会功能，把握社会工作在解决社会问题上的基本价值取向和思路；理解社会工作的基本概念、基本特点及其本质；掌握社会工作的基本知识，包括价值理念、理论基础、过程模式等。\r\n	　　一、社会工作概述\r\n	　　1.社会工作的涵义与性质\r\n	　　2.社会工作的产生与发展\r\n	　　3.社会工作的对象与服务领域\r\n	　　4.社会工作的功能\r\n	　　二、社会工作的价值基础\r\n	　　1.社会福利思想\r\n	　　2.社会福利制度\r\n	　　3.社会工作的价值与价值体系\r\n	　　4.社会工作的专业伦理\r\n	　　三、社会工作的理论\r\n	　　1.社会工作理论的涵义及地位\r\n	　　2.社会工作的基础理论\r\n	　　3.社会工作的实践理论\n', 25);
INSERT INTO `outline` (`id`, `title`, `content`, `sId`) VALUES (84, '2014年中山大学《金融学综合》考研大纲', '\n\r\n	 \n\r\n	　　一、考试性质\r\n	　　《金融学综合》是2014年金融硕士（MF）专业学位研究生入学统一考试的科目之一。《金融学综合》考试要力求反映金融硕士专业学位的特点，科学、公平、准确、规范地测评考生的基本素质和综合能力，选拔具有发展潜力的优秀人才入学，为国家的经济建设培养具有良好职业道德、具有较强分析与解决实际问题能力的高层次、应用型、复合型的金融专业人才。\n\r\n	　　二、考试要求\r\n	　　测试考生对于与金融学和公司财务相关的基本概念、基础理论的掌握和运用能力。\n\r\n	　　三、考试内容\r\n	　　第一部分金融学\r\n	　　一、货币与货币制度\r\n	　　货币的职能与货币制度\r\n	　　国际货币体系\r\n	　　二、利息和利率\r\n	　　利息\r\n	　　利率决定理论\r\n	　　利率的期限结构\r\n	　　三、外汇与汇率\r\n	　　外汇\r\n	　　汇率与汇率制度\r\n	　　币值、利率与汇率\r\n	　　汇率决定理论\n', 25);
