INSERT INTO `outline` (`id`, `title`, `content`, `sId`) VALUES (506, '2015年天津大学0802机械工程考研大纲', '\n\r\n	　　考研网快讯，据天津大学研究生院消息，2015年天津大学机械工程考研大纲已发布，详情如下：\n\n　　240德语\r\n	　　一、考试的总体要求\r\n	　　本课程主要考查考生对德语语言的掌握情况，既考查基础知识也考查运用能力。\r\n	　　二、考试的内容及比例\r\n	　　名词、代词、形容词的变格形式和各格的基本用法，占10%；\r\n	　　动词直陈式主动态的现在时态、现在完成时态、过去时态及其基本用法占10%；\r\n	　　简单句、并列复合句和简单主从复合句的结构和成分，占10%；\r\n	　　常用介词的主要用法，占10%；\r\n	　　直陈式被动态5种时态及其基本用法，占10%；\r\n	　　介词和连词的纵向比较及其用法，占10%；\r\n	　　复杂主从复合句的结构及其用法，占10%；\r\n	　　分词结构的组成及其用法，占10%；\r\n	　　虚拟语气及命令式的基本形式，占10%；\r\n	　　短文写作，占10%。\r\n	　　三、试卷类型及比例\r\n	　　试卷满分为100分。\r\n	　　客观题选择题（4选1）为20分或30分，主观题为80分或70分。\r\n	　　客观题以语法词汇为主；主观题包括语法词汇测试、德汉互译、短文理解及写作等。\r\n	　　四、考试形式及时间\r\n	　　考试形式：笔试。考试时间：3小时。\r\n	 \n', 24);
