INSERT INTO `outline` (`id`, `title`, `content`, `sId`) VALUES (718, '2018年中国海洋大学攻读硕士学位研究生考研大纲', '\n\r\n	　　001海洋与大气学院\n\r\n	　　002信息科学与工程学院\n\r\n	　　003化学化工学院\n\r\n	　　004海洋地球科学学院\n\r\n	　　005水产学院\n\r\n	　　006海洋生命学院\n\r\n	　　007食品科学与工程学院\n\r\n	　　008医药学院.doc\n\r\n	　　009工程学院.doc\n\r\n	　　010环境科学与工程学院.doc\n\r\n	　　011数学科学学院.doc\n\r\n	　　012管理学院.doc\n\r\n	　　013经济学院.doc\n\r\n	　　014外国语学院.doc\n\r\n	　　015文学与新闻传播学院.doc\n\r\n	　　016法政学院.doc\n\r\n	　　017材料科学与工程学院.doc\n\r\n	　　018马克思主义学院.doc\n\r\n	　　019基础教学中心.doc\n\r\n	　　020MBA教育中心.doc\n\r\n	　　021MPA教育中心.doc\n\r\n	　　022会计硕士教育中心.doc\n\r\n	　　024旅游管理硕士教育中心.doc\n\n\n\n　　2018考研预报名即将开始，考研帮手把手教你考研预报名，戳这里获取秘籍\n', 30);
INSERT INTO `outline` (`id`, `title`, `content`, `sId`) VALUES (719, '2018年中国海洋大学考研大纲', '\n\r\n	　　各位考生：\n\r\n	　　为更好的准备2018年硕士研究生招生考试，现将我校硕士研究生招生考试自命题科目考试大纲予以公布。学校2018年硕士招生专业和考试科目的设置，最终以学校9月份公布的《中国海洋大学2018年攻读硕士学位研究生招生专业目录》为准。\n\n\n\n\r\n				学院\n\r\n				专业名称\n\r\n				第一单元科目\n\r\n				第二单元科目\n\r\n				第三单元科目\n\r\n				第四单元科目\n\r\n				复试笔试科目\n\r\n				同等学力加试科目\n\n\n\n\r\n					001海洋与大气学院\n\n考试大纲下载  \n\n\r\n				070601气象学\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				610高等数学\n\r\n				970大气科学综合考试\n\r\n				基础综合（动力气象 天气学 大气物理学 流体力学 普通物理 高等数学 科技英语）\n\r\n				 \n\n\n\r\n				070602大气物理学与大气环境\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				610高等数学\n\r\n				970大气科学综合考试\n\r\n				基础综合（动力气象 天气学 大气物理学 流体力学 普通物理 高等数学 科技英语）\n\r\n				 \n\n\n\r\n				070701物理海洋学\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				610高等数学\n\r\n				803流体力学\n\r\n				基础综合（物理海洋学 流体力学 普通物理 高等数学 科技英语）\n\r\n				 \n\n\n\r\n				0707Z1应用海洋学\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				610高等数学\n\r\n				803流体力学\n\r\n				基础综合（物理海洋学 流体力学 普通物理 高等数学 科技英语）\n\r\n				 \n\n\n\r\n				0707Z2海洋资源与权益综合管理\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				610高等数学\n\r\n				804海洋学\n\r\n				基础综合（海洋学 高等数学 普通物理 科技英语）\n\r\n				 \n\n\n\n\r\n					002信息科学与工程学院\n\n考试大纲下载  \n\r\n					  \n\n\r\n				070205凝聚态物理\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				638量子力学\n\r\n				806普通物理\n\r\n				现代物理基础、科技英语\n\r\n				 \n\n\n\r\n				070206声学\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				301数学一\n\r\n				953声学基础\n\r\n				数字信号处理\n\r\n				 \n\n\n\r\n				070207光学\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				638量子力学\n\r\n				806普通物理\n\r\n				现代物理基础、科技英语\n\r\n				 \n\n\n\r\n				070503地图学与地理信息系统\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				610高等数学\n\r\n				808地理信息系统\n\r\n				C++语言编程、科技英语\n\r\n				 \n\n\n\r\n				0707Z3海洋探测技术\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				610高等数学\n\r\n				810数字电子技术\n\r\n				光学电磁学或信号与系统、科技英语\n\r\n				 \n\n\n\r\n				080300光学工程\n\r\n				101思想政治理论\n\r\n				201英语一\n\r\n				301数学一\n\r\n				806普通物理\n\r\n				现代光学基础或电子技术基础、科技英语\n\r\n				 \n\n\n\r\n				081001通信与信息系统\n\r\n		