INSERT INTO `reference_book` (`id`, `title`, `content`, `sId`) VALUES (293, '2018年中南民族大学硕士研究生入学考试初试、复试科目设置及参考书目一览表', '\n\n\n\n\n\n所属学院 \n\n\n\n一级学科名称 \n\n\n\n二级学科名称及研究方向 \n\n\n\n第一、二单元科目及参考书目 \n\n\n\n第三单元科目及 \n参考书目 \n\n\n\n第四单元科目及参考书目 \n\n\n\n复试科目及参考书目 \n\n\n\n加试科目及参考书目 \n\n\n\n\n\n001法学院（027-67842770）\n\n\n\n0101哲学 \n\n\n\n010102 中国哲学 \r\n					01中国古代哲学\r\n					02中国近现代哲学\n\n\n\n①▲101思想政治理论 \n②▲201英语一\n\r\n					全国统考科目 \n\n\n\n611西方哲学史 \r\n					参考书目：\r\n					1）邓晓芒、赵林主编：《西方哲学史》，高等教育出版社，2014年版。\r\n					2）赵敦华：《西方哲学简史》，北京大学出版社，2012年版。\n\n\n\n801中国哲学史 \r\n					参考书目：\r\n					1)郭齐勇：《中国哲学史》，高等教育出版社，2006年版。\r\n					2)冯友兰：《中国哲学简史》，涂又光译（版本不限）。 \n\n\n\n宋明理学 \n\r\n					不指定参考书\n\n\n\n①西方近代哲学史 \n②马克思主义哲学原理 \n\n\r\n						邓晓芒、赵林主编：《西方哲学史》，高等教育出版社，2014年版。\n\n\r\n					2）陶德麟、汪信砚主编：《马克思主义哲学原理》，人民出版社，2010年版。\n\n\n\n\n\n0301 法学\n\n\n\n030101法学理论 \r\n					01社会主义法治的理论与实践\r\n					02法律文化\r\n					03法社会学\n\n\n\n612法学专业基础 （法理学、宪法学） \r\n					参考书目：\r\n					1）张文显主编：《法理学》，高等教育出版社，2013年版。\r\n					2）周叶中主编：《宪法》（第三版），高等教育出版社，2011年版 。\n\n\n\n802法学理论专业综合（中外法律思想史） \r\n					参考书目：\r\n					1）马小红、姜晓敏主编：《中国法律思想史》，中国人民大学出版社，第三版。                          \r\n					2）严存生主编：《西方法律思想史》，法律出版社，2013年版。\n\n\n\n社会主义法治理论 \r\n					同初试“法理学”“法学理论专业综合”参考书目\n\n\n\n①法理学 \n②西方法律思想史 \r\n					参考书目：\r\n					1）张文显主编：《法理学》，高等教育出版社，2013年版。\r\n					2）严存生主编：《西方法律思想史》，法律出版社，2013年版。\n\n\n\n\n\n030103宪法学与行政法学 \r\n					01宪法学（宪法学基础理论、宪政与人权）\r\n					02 行政法学（行政法治）\n\n\n\n803宪法学与行政法学专业综合（宪法学、行政法与行政诉讼法） \r\n					参考书目：\r\n					1）周叶中主编：《宪法》（第二版），北京大学出版社、高等教育出版社，2005年版 。 \r\n					2）姜明安主编：《行政法与行政诉讼法》，北京大学出版社、高等教育出版社，2005年版 。 \r\n					3）潘弘祥：《宪法的社会理论分析》，人民出版社，2009年版。\n\n\n\n①政治学基础 \n②比较宪法与比较行政法 \r\n					参考书目：\r\n					1）王浦劬：《政治学基础》，北京大学出版社，2006年版\r\n					2）韩大元《比较宪法学》，高教出版社，2008年版。\r\n					3）张千帆、赵娟、黄建军《比较行政法：体系、制度与过程》，法律出版社，2008年版。\n\n\n\n①人权法学 \n②立法学 \r\n					参考书目：\r\n					1）李步云主编：《人权法学》，法律出版社，2005年版。\r\n					2）周旺生著：《立法学》，法律出版社，2004年版。\n\n\n\n\n\n030104 刑法学\r\n					01中国刑法\r\n					02国际刑法\n\n\n\n804刑法学专业综合（刑法学、刑事诉讼法学） \r\n					参考书目：\r\n					1）张明楷著：《刑法学教程》，北京