INSERT INTO `reference_book` (`id`, `title`, `content`, `sId`) VALUES (393, '2015年湖南大学081402结构工程考研参考书目与考试科目', '\n\r\n	　　考研论坛根据15年报考湖南大学结构工程学长学姐的参考书使用情况，为16年的考研er整理了专业课的参考书目。\n\r\n	　　湖南大学结构工程考研专业课为801结构力学，据该校的研究生院公布，不指定官方参考书目。但是，每年的考试内容都是来自以下大家认可的几本书。\n\r\n	　　▶推荐复习参考书\n\n\n\n\r\n				书名\n\r\n				作者\n\r\n				出版社\n\n\n\r\n				《结构力学》(第四版)\n\r\n				杨二康，李家宝主编\n\r\n				高等教育出版社\n\n\n\n', 21);
INSERT INTO `reference_book` (`id`, `title`, `content`, `sId`) VALUES (394, '2015年湖南大学125300会计硕士考研参考书目与考试科目', '\n\r\n	　　考研网快讯，据湖南大学研究生院消息，2016年湖南大学会计硕士125300考研参考书目与考试科目已发布，详情如下：\n\n\n\n\r\n				125300会计硕士（专业学位）\n\r\n				初试考试科目\n\r\n				复试考试科目\n\n\n\n\r\n					1、199管理类联考综合能力\n\r\n					2、204英语二\n\n\r\n				F1407会计学综合\n\n\n\n\r\n	　　►初试：\r\n	　　199管理类联考综合能力，属于全国统一命题，所以是不存在高校指定用书的。但近年来参考书目多由教育部考试中心提供。因此，教育部考试中心的考试大纲和命题意见，是最基本的，用高等教育出版社系列即可，包括：《高教版考研大纲MBA、MPA、MPAcc管理类联考综合能力考试大纲解析》，全国管理类联考大纲配套教材专家委员会编写；《高教版考研大纲MBA、MPA、MPAcc管理类联考综合能力考试大纲》，全国管理类联考大纲配套教材专家委员会编写。\n\r\n	　　配合用书，目前市面上比较流行的有中国人民大学出版社的考试系列用书，主要包括以下四册，关于历年真题分类精解可根据个人需要：\r\n	　　《全国管理类专业学位联考综合能力考试指南》，全国专业学位联考辅导用书编写组编；\r\n	　　《管理类专业学位联考综合能力考试：试题归类解析及知识点清单(写作分册)》，刘岩编；\r\n	　　《管理类专业学位联考综合能力考试试题归类解析及知识点清单：逻辑分册》，李雪编；\r\n	　　《管理类专业学位联考综合能力考试试题归类解析及知识点清单：数学分册》，童武编。\r\n	　　另外，机械工业出版社出版的一些列丛书也广受考研学子的青睐，例如：\r\n	　　《MBA、MPA、MPAcc管理类联考高分一本通关》(数学+逻辑+写作+英语二)，汪海洋，汪学能，赵鑫全编。\n\r\n	　　►复试：\r\n	　　1、《中级财务会计学》，王善平、汤健、刘天雄主编，湖南人民出版社；\r\n	　　2、《财务管理学》，朱开悉、何进日主编，湖南人民出版社；\r\n	　　3、《成本会计学》，周仁仪、朱启明主编，湖南人民出版社。\n\n【精品阅读】\n湖大硕士就业形势分析：会计硕士\n湖大硕士报考难度分析：会计硕士\n湖大会计专硕经验：把握中财终成功\n湖大研究生专业介绍：会计硕士\n', 21);
